mapper.select:
SELECT
	   count(*)
  FROM
	    favdoc
 where SIGNNO = #{signNo}
  and eno = #{eno}

mapper.update:
INSERT INTO favdoc (
				    regdate
				   ,signno
				   ,eno
				) VALUES (
				    sysdate
				   ,#{signNo}
				   ,#{eno}
				)

DELETE FROM favdoc
WHERE
signNo =#{signNo}
AND eno = #{eno}
