mapper.select:
select eno 
  from employee 
 where name = #{name}

select name 
  from employee 
 where eno = #{eno}

mapper.update:
update mto
   set mtoImpor = 1
 where
   mailno = #{mailNo}
   and eno = #{eno}

update mto
   set mtoImpor = 0
 where
   mailno = #{mailNo}
   and eno = #{eno}

update mto
   set mtoDel = 1
 where mailno = #{mailNo}
   and eno = #{eno}

update mail
   set mFromDel = 1
 where mailno = #{mailNo}
   and mfrom = #{mFrom}

UPDATE mto
   SET
        mtoCheck = 1
 WHERE
    mailno = #{mailNo}
   and eno = #{mFrom}

UPDATE mto
   SET
        mtoCheck = 1
 WHERE
    mailno = #{mailNo}
   and eno = #{eno}
