mapper.update:
update manual
set
manualtitle = #{manualTitle}
,manualcontent = #{manualContent}
,updatedate = sysdate
,cateno = #{cateNo}
where
manualno = #{manualNo}

mapper.select:
SELECT
count(*)
FROM
favmanual
where manualno = #{manualNo}
and ENO = #{eNo}

mapper.update:
DELETE FROM favmanual
WHERE
manualno =#{manualNo}
AND eno = #{eNo}
