sql.if:
			AND a.wcode in (SELECT b.wcode
                         FROM workmanager b, employee c
                         WHERE b.eno = c.eno
                           AND c.name = #{keyword}
                                )

			AND a.wcode in (SELECT b.wcode
                         FROM work b, employee c
                         WHERE b.eno = c.eno
                           AND c.name = #{keyword}
                                )

mapper.update:
update knowhow
set viewcnt = viewcnt + 1
where wcode = #{wcode}

INSERT INTO favknowhow (
    regdate
   ,wcode
   ,eno
) VALUES (
    sysdate
   ,#{wcode}
   ,#{eno}
)

delete from favknowhow
where wcode = #{wcode}
and eno = #{eno}

mapper.select:
select
	count(*)
from favknowhow
where wcode = #{wcode}
and eno = #{eno}
