mapper.update:
  		INSERT INTO qna (
		    qnano
		   ,wcode
		   ,eno
		   ,title
		   ,content
		   ,regdate
		) VALUES (
		    qna_seq.nextval
		   ,#{wcode}
		   ,#{eno}
		   ,#{title}
		   ,#{content}
		   ,sysdate
		)

  		INSERT INTO qnareply (
		    qrno
		   ,qnano
		   ,content
		   ,eno
		   ,regdate
		) VALUES (
		    qnaReply_seq.nextval
		   ,#{qnano}
		   ,#{content}
		   ,#{eno}
		   ,sysdate
		)

  		UPDATE qna
		    SET
		        title = #{title},
		        content = #{content}
		WHERE
		    qnano =#{qnaNo}

  		UPDATE qnareply
		    SET
		        content = #{content}
		WHERE
		    qrno = #{qrno}
